import { NextRequest, NextResponse } from "next/server";

export const dynamic = 'force-dynamic';

export async function POST(request: NextRequest) {
    try {
        const response = NextResponse.json(
            {
                success: true,
                message: "Logout successful",
            },
            { status: 200 }
        );

        // Clear the auth cookie
        response.cookies.set({
            name: "auth-token",
            value: "",
            httpOnly: true,
            secure: process.env.NODE_ENV === "production",
            sameSite: "lax",
            maxAge: 0,
            path: "/",
        });

        return response;
    } catch (error) {
        console.error("Logout error:", error);
        return NextResponse.json(
            {
                success: false,
                error: "An error occurred during logout",
            },
            { status: 500 }
        );
    }
}

import { NextResponse } from "next/server";
export const dynamic = 'force-dynamic';
import dbConnect, { Role, ActionType, logEngagement, LeanProject } from "@/lib/db";
import { Project, ProjectMember } from "@/lib/models";
import mongoose from "mongoose";

// ============================================
// POST /api/projects/create
// ============================================
export async function POST(request: Request) {
    try {
        const userId = request.headers.get("x-user-id");
        const userRole = request.headers.get("x-user-role");

        if (!userId) {
            return NextResponse.json(
                { error: "Unauthorized: You must be logged in" },
                { status: 401 }
            );
        }

        if (userRole !== Role.MENTOR) {
            return NextResponse.json(
                { error: "Only mentors can create projects" },
                { status: 403 }
            );
        }

        const body = await request.json();
        const { title } = body;

        if (!title || title.trim() === "") {
            return NextResponse.json(
                { error: "Project title is required" },
                { status: 400 }
            );
        }

        const trimmedTitle = title.trim();

        await dbConnect();

        const session = await mongoose.startSession();
        let result;

        try {
            session.startTransaction();

            const projectBatch = await Project.create([{
                title: trimmedTitle,
                mentorId: userId,
            }], { session });

            result = projectBatch[0] as unknown as LeanProject;

            await ProjectMember.create([{
                userId: userId,
                projectId: result._id,
            }], { session });

            await session.commitTransaction();
        } catch (txnError) {
            await session.abortTransaction();
            throw txnError;
        } finally {
            session.endSession();
        }

        return NextResponse.json(
            {
                projectId: result._id.toString(),
                title: result.title,
                message: "Project created successfully!",
                createdAt: result.createdAt,
            },
            { status: 201 }
        );

    } catch (err) {
        console.error("Error in /api/projects/create:", err);
        const errorMessage = err instanceof Error ? err.message : "Internal server error";
        return NextResponse.json(
            {
                error: "Internal server error",
                details: errorMessage,
            },
            { status: 500 }
        );
    }
}

import { NextResponse } from "next/server";
export const dynamic = 'force-dynamic';
import dbConnect, { LeanUser } from "@/lib/db";
import { User } from "@/lib/models";

/**
 * GET /api/users
 * Returns all users in the system
 * For mentors: Returns all students (to add to projects)
 * For students: Returns all users (for peer feedback)
 */
export async function GET(request: Request) {
    try {
        const userId = request.headers.get("x-user-id");

        if (!userId) {
            return NextResponse.json(
                { error: "Unauthorized: Missing authentication" },
                { status: 401 }
            );
        }

        const { searchParams } = new URL(request.url);
        const roleFilter = searchParams.get("role");

        await dbConnect();

        const query: any = {};
        if (roleFilter && (roleFilter === "STUDENT" || roleFilter === "MENTOR")) {
            query.role = roleFilter;
        }

        const userDocs = await User.find(query)
            .select('name email role createdAt')
            .sort({ name: 1 })
            .lean() as LeanUser[];

        const users = userDocs.map((u: LeanUser) => ({
            id: u._id.toString(),
            name: u.name,
            email: u.email,
            role: u.role,
            createdAt: u.createdAt,
        }));

        return NextResponse.json(users, { status: 200 });

    } catch (err) {
        console.error("Error in users API:", err);
        return NextResponse.json(
            { error: "Internal server error" },
            { status: 500 }
        );
    }
}

'use client';

/**
 * ============================================
 * *STUDENT DASHBOARD (ANIMATED)
 * *Tech Stack: Next.js App Router, TypeScript, CSS, Framer Motion
 * *Design: Staggered Entrance, Hover Effects, Clean SaaS
 * ============================================
 */

import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/contexts/AuthContext';
import { motion, AnimatePresence, Variants } from 'framer-motion';
import {
    Projector,
    CheckSquare,
    MessageSquare,
    TrendingUp,
    LogOut,
    Clock,
    Search,
    Bell
} from 'lucide-react';
import './StudentDashboard.css';

// --- TYPE DEFINITIONS ---

interface Project {
    id: string;
    name: string;
    description: string;
    status: 'In Progress' | 'Completed' | 'Pending';
}

interface Task {
    id: string;
    title: string;
    dueDate: string;
    status: 'Todo' | 'Doing' | 'Done';
}

interface Feedback {
    id: string;
    fromUser: string;
    project: string;
    comment: string;
    date: string;
}

interface Analytics {
    totalProjects: number;
    totalTasks: number;
    feedbackCount: number;
    completionRate: number;
}

// --- ANIMATION VARIANTS ---

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1,
        },
    },
};

const itemVariants: Variants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: { type: 'spring', stiffness: 300, damping: 24 } as unknown as any,
    },
};

const cardVariants: Variants = {
    hidden: { scale: 0.95, opacity: 0 },
    visible: {
        scale: 1,
        opacity: 1,
        transition: { type: 'spring', stiffness: 300, damping: 24 } as unknown as any,
    },
};

// --- MAIN COMPONENT ---

export default function StudentDashboard() {
    const { user, loading: authLoading } = useAuth();
    const router = useRouter();

    // State for data
    const [projects, setProjects] = useState<Project[]>([]);
    const [tasks, setTasks] = useState<Task[]>([]);
    const [feedbacks, setFeedbacks] = useState<Feedback[]>([]);
    const [analytics, setAnalytics] = useState<Analytics | null>(null);
    const [searchQuery, setSearchQuery] = useState('');

    // UI State
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Role-based access control
    useEffect(() => {
        if (!authLoading) {
            if (!user) {
                router.push('/login');
            } else if (user.role !== 'STUDENT') {
                router.push('/dashboard/mentor');
            }
        }
    }, [user, authLoading, router]);

    /**
     * Data Fetching Logic (Real API Calls)
     */
    useEffect(() => {
        const fetchData = async () => {
            if (!user) return; // Wait for user to be loaded

            try {
                setIsLoading(true);
                setError(null);

                // Prepare auth headers
                const headers = {
                    'x-user-id': user.id,
                    'x-user-role': user.role,
                };

                // Fetch all data in parallel
                const [projectsRes, tasksRes, feedbackRes] = await Promise.all([
                    fetch('/api/projects', { headers }),
                    fetch('/api/tasks', { headers }),
                    fetch('/api/feedback', { headers }),
                ]);

                // Check for errors
                if (!projectsRes.ok || !tasksRes.ok || !feedbackRes.ok) {
                    throw new Error('Failed to fetch dashboard data');
                }

                // Parse responses
                const projectsData = await projectsRes.json();
                const tasksData = await tasksRes.json();
                const feedbackData = await feedbackRes.json();

                // Transform projects data to match component interface
                const transformedProjects: Project[] = projectsData.map((p: any) => {
                    // Get all tasks for this project
                    const projectTasks = tasksData.filter((t: any) => t.projectId === p.id);
                    const totalTasks = projectTasks.length;
                    const completedTasks = projectTasks.filter((t: any) => t.status === 'DONE').length;

                    // Determine status based on task completion
                    let status: 'In Progress' | 'Completed' | 'Not Started';
                    if (totalTasks === 0) {
                        status = 'Not Started';
                    } else if (completedTasks === totalTasks) {
                        status = 'Completed';
                    } else {
                        status = 'In Progress';
                    }

                    return {
                        id: p.id,
                        name: p.title,
                        description: `Project created on ${new Date(p.createdAt).toLocaleDateString()}`,
                        status,
                    };
                });

                // Transform tasks data to match component interface
                const transformedTasks: Task[] = tasksData.map((t: any) => ({
                    id: t.id,
                    title: t.title,
                    dueDate: new Date(t.createdAt).toLocaleDateString(),
                    status: t.status === 'TODO' ? 'Todo' : t.status === 'IN_PROGRESS' ? 'Doing' : 'Done',
                }));

                // Transform feedback data to match component interface
                const transformedFeedback: Feedback[] = feedbackData.map((f: any) => ({
                    id: f.id,
                    fromUser: f.fromUser?.name || 'Unknown User',
                    project: f.project?.title || 'Unknown Project',
                    comment: f.comment || 'No comment provided',
                    date: new Date(f.createdAt).toLocaleDateString(),
                }));

                setProjects(transformedProjects);
                setTasks(transformedTasks);
                setFeedbacks(transformedFeedback);
                setAnalytics({
                    totalProjects: transformedProjects.length,
                    totalTasks: transformedTasks.length,
                    feedbackCount: transformedFeedback.length,
                    completionRate: tasksData.filter((t: { status: string }) => t.status === 'DONE').length > 0
                        ? Math.round((tasksData.filter((t: { status: string }) => t.status === 'DONE').length / tasksData.length) * 100)
                        : 0,
                });

            } catch (err) {
                console.error('Dashboard fetch error:', err);
                setError('Failed to fetch dashboard data. Please try again.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, [user]);

    if (isLoading) {
        return (
            <div className="loadingContainer">
                <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    className="spinner"
                />
                <p>Prepping your workspace...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="errorContainer">
                <p className="errorText">{error}</p>
                <button onClick={() => window.location.reload()} className="viewBtn">Try Again</button>
            </div>
        );
    }

    return (
        <motion.div
            initial="hidden"
            animate="visible"
            variants={containerVariants}
            className="dashboardContainer"
        >
            {/* Navbar Section */}
            <motion.nav variants={itemVariants} className="navbar">
                <div className="logo">CollabLearn</div>
                <div className="navRight">
                    <div className="searchBar" style={{ position: 'relative', display: 'flex', alignItems: 'center' }}>
                        <Search size={16} style={{ position: 'absolute', left: '12px', color: '#94a3b8' }} />
                        <input
                            type="text"
                            placeholder="Search..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            style={{ padding: '0.6rem 1rem 0.6rem 2.5rem', borderRadius: '10px', border: '1px solid #e2e8f0', background: '#f8fafc', fontSize: '0.875rem', width: '200px' }}
                        />
                    </div>
                    <motion.div whileHover={{ scale: 1.1 }} style={{ cursor: 'pointer', color: '#64748b' }}>
                        <Bell size={20} />
                    </motion.div>
                    <Link href="/give-feedback" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', padding: '0.5rem 1rem', background: '#6366f1', color: 'white', borderRadius: '10px', textDecoration: 'none', fontSize: '0.875rem', fontWeight: 600 }}>
                        <MessageSquare size={18} />
                        Give Feedback
                    </Link>
                    <Link href="/feedback" style={{ display: 'flex', alignItems: 'center', color: '#64748b', textDecoration: 'none', marginRight: '1rem', fontSize: '0.875rem' }}>
                        <MessageSquare size={18} style={{ marginRight: '6px' }} />
                        My Feedback
                    </Link>
                    <span className="welcomeText">Welcome back, {user?.name || 'Student'}!</span>
                    <Link href="/logout" className="logoutBtn">
                        <LogOut size={16} style={{ display: 'inline', marginRight: '6px', verticalAlign: 'middle' }} />
                        Logout
                    </Link>
                </div>
            </motion.nav>

            {/* Analytics Cards */}
            <div className="analyticsGrid">
                {[
                    { title: 'Total Projects', value: analytics?.totalProjects, color: '#2563eb', icon: <Projector size={20} /> },
                    { title: 'Total Tasks', value: analytics?.totalTasks, color: '#8b5cf6', icon: <CheckSquare size={20} /> },
                    { title: 'Feedback Received', value: analytics?.feedbackCount, color: '#ec4899', icon: <MessageSquare size={20} /> },
                    { title: 'Completion %', value: `${analytics?.completionRate}%`, color: '#10b981', icon: <TrendingUp size={20} /> },
                ].map((card, idx) => (
                    <motion.div
                        key={idx}
                        variants={cardVariants}
                        whileHover={{ y: -5, transition: { duration: 0.2 } }}
                        className="analyticsCard"
                        style={{ borderColor: card.color }}
                    >
                        <div className="cardHeader" style={{ display: 'flex', justifyContent: 'space-between', color: card.color }}>
                            <div className="cardTitle">{card.title}</div>
                            {card.icon}
                        </div>
                        <div className="cardNumber">{card.value}</div>
                    </motion.div>
                ))}
            </div>

            {/* Main Grid Content */}
            <div className="contentGrid">

                {/* Projects List */}
                <motion.section variants={itemVariants} className="section">
                    <div className="sectionTitle">Your Projects</div>
                    <div className="list">
                        <AnimatePresence>
                            {projects
                                .filter(project =>
                                    searchQuery === '' ||
                                    project.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                                    project.description.toLowerCase().includes(searchQuery.toLowerCase())
                                )
                                .map((project, idx) => (
                                    <motion.div
                                        key={project.id}
                                        initial={{ opacity: 0, x: -20 }}
                                        animate={{ opacity: 1, x: 0 }}
                                        transition={{ delay: idx * 0.1 }}
                                        className="listItem"
                                    >
                                        <div className="itemInfo">
                                            <h4 style={{ fontSize: '0.95rem' }}>{project.name}</h4>
                                            <p>{project.description}</p>
                                            <span className={`badge ${project.status === 'Completed' ? 'badgeCompleted' : project.status === 'In Progress' ? 'badgeProgress' : 'badgePending'}`}>
                                                {project.status}
                                            </span>
                                        </div>
                                        <Link href={`/projects/${project.id}`} className="viewBtn">
                                            View
                                        </Link>
                                    </motion.div>
                                ))}
                        </AnimatePresence>
                    </div>
                </motion.section>

                {/* Tasks & Feedback Column */}
                <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>

                    {/* Tasks List */}
                    <motion.section variants={itemVariants} className="section">
                        <div className="sectionTitle">Your Tasks</div>
                        <div className="list">
                            {tasks.map((task, idx) => (
                                <motion.div
                                    key={task.id}
                                    initial={{ opacity: 0, x: 20 }}
                                    animate={{ opacity: 1, x: 0 }}
                                    transition={{ delay: idx * 0.1 }}
                                    className="listItem"
                                >
                                    <div className="itemInfo">
                                        <h4 style={{ fontSize: '0.95rem' }}>{task.title}</h4>
                                        <p style={{ display: 'flex', alignItems: 'center', gap: '0.3rem' }}>
                                            <Clock size={12} /> {task.dueDate}
                                        </p>
                                        <span style={{ fontSize: '0.75rem', fontWeight: 600, color: task.status === 'Done' ? '#10b981' : '#f59e0b' }}>
                                            {task.status}
                                        </span>
                                    </div>
                                    <Link href={`/tasks/${task.id}`} className="viewBtn" style={{ background: '#f1f5f9', color: '#475569' }}>
                                        View
                                    </Link>
                                </motion.div>
                            ))}
                        </div>
                    </motion.section>

                    {/* Feedback Section */}
                    <motion.section variants={itemVariants} className="section">
                        <div className="sectionTitle">Recent Feedback</div>
                        <div className="list">
                            <AnimatePresence>
                                {feedbacks.map((item: Feedback, idx: number) => (
                                    <motion.div
                                        key={item.id}
                                        initial={{ opacity: 0, y: 10 }}
                                        animate={{ opacity: 1, y: 0 }}
                                        transition={{ delay: idx * 0.1 }}
                                        className="feedbackItem"
                                    >
                                        <div className="feedbackHeader">
                                            <span className="mentorName">{item.fromUser}</span>
                                            <span style={{ fontSize: '0.75rem', color: '#94a3b8' }}>{item.project}</span>
                                        </div>
                                        <p className="feedbackText">&quot;{item.comment}&quot;</p>
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </div>
                    </motion.section>

                </div>
            </div>
        </motion.div>
    );
}

'use client';

/**
 * ============================================
 * CREATE FEEDBACK PAGE (ANIMATED)
 * Clean, Production-Quality Form with Framer Motion
 * Tech Stack: Next.js App Router, TypeScript, CSS
 * ============================================
 */

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { motion, AnimatePresence } from 'framer-motion';
import { Variants } from "framer-motion";

import {
    ArrowLeft,
    Send,
    User,
    Briefcase,
    MessageSquare,
    AlertCircle,
    CheckCircle2
} from 'lucide-react';
import './CreateFeedback.css';

// --- TYPE DEFINITIONS ---

interface Member {
    id: string;
    name: string;
    role: string;
}

interface Project {
    id: string;
    name: string;
}

// --- ANIMATION VARIANTS ---

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1,
        },
    },
};

const itemVariants: Variants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: { type: 'spring' as const, stiffness: 300, damping: 24 },
    },
};

const alertVariants = {
    hidden: { opacity: 0, height: 0, marginBottom: 0 },
    visible: { opacity: 1, height: 'auto', marginBottom: '1.5rem' },
    exit: { opacity: 0, height: 0, marginBottom: 0 },
};

// --- MAIN COMPONENT ---

export default function CreateFeedbackPage() {
    const router = useRouter();

    // Data State
    const [members, setMembers] = useState<Member[]>([]);
    const [projects, setProjects] = useState<Project[]>([]);

    // Form State
    const [selectedMemberId, setSelectedMemberId] = useState('');
    const [selectedProjectId, setSelectedProjectId] = useState('');
    const [feedbackText, setFeedbackText] = useState('');

    // UI State
    const [isPageLoading, setIsPageLoading] = useState(true);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [success, setSuccess] = useState(false);

    /**
     * Fetch initial data for dropdowns
     */
    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                setIsPageLoading(true);
                // Simulating API Latency
                await new Promise((resolve) => setTimeout(resolve, 800));

                // Mock Members
                const mockMembers: Member[] = [
                    { id: 'm1', name: 'Sarah Connor', role: 'Senior Mentor' },
                    { id: 'm2', name: 'James Bond', role: 'Project Lead' },
                    { id: 'm3', name: 'Alan Turing', role: 'Technical Mentor' },
                ];

                // Mock Projects
                const mockProjects: Project[] = [
                    { id: 'p1', name: 'Web Engagement App' },
                    { id: 'p2', name: 'AI Study Assistant' },
                    { id: 'p3', name: 'Campus Maps' },
                ];

                setMembers(mockMembers);
                setProjects(mockProjects);
            } catch (_err) {
                setError('Failed to load form data. Please refresh.');
            } finally {
                setIsPageLoading(false);
            }
        };

        fetchInitialData();
    }, []);

    /**
     * Form Submission Handler
     */
    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        // Simple Validation
        if (!selectedMemberId || !selectedProjectId || !feedbackText.trim()) {
            setError('Please fill in all fields before submitting.');
            return;
        }

        try {
            setIsSubmitting(true);
            setError(null);

            // Simulating API POST request
            await new Promise((resolve) => setTimeout(resolve, 1500));

            setSuccess(true);

            // Redirect to feedback list after 2 seconds
            setTimeout(() => {
                router.push('/feedback');
            }, 2000);

        } catch (_err) {
            setError('Something went wrong during submission. Please try again.');
        } finally {
            setIsSubmitting(false);
        }
    };

    if (isPageLoading) {
        return (
            <div className="formContainer pageLoading">
                <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
                    className="pageSpinner"
                />
            </div>
        );
    }

    return (
        <motion.div
            initial="hidden"
            animate="visible"
            variants={containerVariants}
            className="formContainer"
        >
            {/* Header */}
            <motion.header variants={itemVariants} className="header">
                <Link href="/feedback" className="backBtn">
                    <ArrowLeft size={16} /> Back to Feedback List
                </Link>
                <h1 className="title">Give Feedback</h1>
            </motion.header>

            {/* Form Card */}
            <motion.div variants={itemVariants} className="formCard">
                <AnimatePresence mode="wait">
                    {error && (
                        <motion.div
                            key="error"
                            variants={alertVariants}
                            initial="hidden"
                            animate="visible"
                            exit="exit"
                            className="errorAlert"
                        >
                            <AlertCircle size={20} /> {error}
                        </motion.div>
                    )}

                    {success && (
                        <motion.div
                            key="success"
                            variants={alertVariants}
                            initial="hidden"
                            animate="visible"
                            exit="exit"
                            className="successAlert"
                        >
                            <CheckCircle2 size={20} /> Feedback submitted successfully! Redirecting...
                        </motion.div>
                    )}
                </AnimatePresence>

                <form onSubmit={handleSubmit}>
                    {/* Member Selection */}
                    <motion.div variants={itemVariants} className="formGroup">
                        <label className="label">Select Member (Mentor/Peer)</label>
                        <div style={{ position: 'relative' }}>
                            <select
                                className="select"
                                value={selectedMemberId}
                                onChange={(e) => setSelectedMemberId(e.target.value)}
                                disabled={isSubmitting || success}
                            >
                                <option value="">Choose a member...</option>
                                {members.map(m => (
                                    <option key={m.id} value={m.id}>{m.name} ({m.role})</option>
                                ))}
                            </select>
                            <User size={18} style={{ position: 'absolute', right: '35px', top: '12px', color: '#94a3b8', pointerEvents: 'none' }} />
                        </div>
                    </motion.div>

                    {/* Project Selection */}
                    <motion.div variants={itemVariants} className="formGroup">
                        <label className="label">Select Project</label>
                        <div style={{ position: 'relative' }}>
                            <select
                                className="select"
                                value={selectedProjectId}
                                onChange={(e) => setSelectedProjectId(e.target.value)}
                                disabled={isSubmitting || success}
                            >
                                <option value="">Choose a project...</option>
                                {projects.map(p => (
                                    <option key={p.id} value={p.id}>{p.name}</option>
                                ))}
                            </select>
                            <Briefcase size={18} style={{ position: 'absolute', right: '35px', top: '12px', color: '#94a3b8', pointerEvents: 'none' }} />
                        </div>
                    </motion.div>

                    {/* Feedback Text */}
                    <motion.div variants={itemVariants} className="formGroup">
                        <label className="label">Your Feedback / Comments</label>
                        <div style={{ position: 'relative' }}>
                            <textarea
                                className="textarea"
                                placeholder="Write your feedback here..."
                                value={feedbackText}
                                onChange={(e) => setFeedbackText(e.target.value)}
                                disabled={isSubmitting || success}
                            />
                            <MessageSquare size={18} style={{ position: 'absolute', right: '15px', top: '12px', color: '#94a3b8', pointerEvents: 'none' }} />
                        </div>
                    </motion.div>

                    {/* Submit Button */}
                    <motion.button
                        variants={itemVariants}
                        type="submit"
                        className="submitBtn"
                        disabled={isSubmitting || success}
                        whileHover={{ scale: success ? 1 : 1.02 }}
                        whileTap={{ scale: success ? 1 : 0.98 }}
                    >
                        {isSubmitting ? (
                            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
                                <motion.div
                                    animate={{ rotate: 360 }}
                                    transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
                                    className="spinner"
                                />
                                Submitting...
                            </div>
                        ) : (
                            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
                                <Send size={18} /> Submit Feedback
                            </div>
                        )}
                    </motion.button>
                </form>
            </motion.div>
        </motion.div>
    );
}

'use client';

/**
 * ============================================
 * FEEDBACK LIST PAGE (Students & Mentors)
 * High-Fidelity, Animated SaaS Experience
 * Tech Stack: Next.js App Router, TypeScript, Framer Motion
 * ============================================
 */

import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { motion, AnimatePresence } from 'framer-motion';
import { Variants } from "framer-motion";
import { useAuth, getAuthHeaders } from '@/contexts/AuthContext';

import {
    ArrowLeft,
    MessageSquare,
    User,
    Briefcase,
    AlertCircle,
    Inbox
} from 'lucide-react';
import './FeedbackList.css';

// --- TYPE DEFINITIONS ---

interface Feedback {
    id: string;
    mentorName: string;
    projectName: string;
    comment: string;
    date: string;
}

// --- ANIMATION VARIANTS ---

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.15, // Delay between each card's entrance
        },
    },
};

const cardVariants: Variants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: { type: 'spring' as const, stiffness: 260, damping: 20 },
    },
};

// --- MAIN COMPONENT ---

export default function FeedbackPage() {
    const router = useRouter();
    const { user } = useAuth();
    const [feedbacks, setFeedbacks] = useState<Feedback[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    /**
     * Data Fetching Logic (Real API)
     * Fetches all feedback for the student/mentor from backend
     */
    useEffect(() => {
        const fetchAllFeedback = async () => {
            if (!user) {
                setError('Not authenticated');
                setIsLoading(false);
                return;
            }

            try {
                setIsLoading(true);
                setError(null);

                const headers = getAuthHeaders(user);

                // Fetch feedback from API
                const res = await fetch('/api/feedback', { headers });

                if (!res.ok) {
                    throw new Error('Failed to fetch feedback');
                }

                const feedbackData = await res.json();

                // Transform API data to match component interface
                const transformedFeedback: Feedback[] = feedbackData.map((f: { id: string; comment?: string; createdAt: string }) => ({
                    id: f.id,
                    mentorName: 'Peer', // We'd need to fetch user names separately
                    projectName: 'Project', // We'd need to fetch project names separately
                    comment: f.comment || 'No comment provided',
                    date: new Date(f.createdAt).toLocaleDateString()
                }));

                setFeedbacks(transformedFeedback);

            } catch (err: unknown) {
                setError(err instanceof Error ? err.message : 'Failed to fetch feedback. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchAllFeedback();
    }, [user]);

    // --- RENDERING HELPERS ---

    if (isLoading) {
        return (
            <div className="loadingContainer">
                <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
                    className="spinner"
                />
                <p>Loading your feedback...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="errorContainer">
                <AlertCircle size={48} color="#ef4444" style={{ marginBottom: '1rem' }} />
                <p className="errorText">{error}</p>
                <button onClick={() => window.location.reload()} className="backBtn" style={{ marginTop: '1rem', cursor: 'pointer', background: 'none', border: 'none' }}>
                    ΓåÉ Try Again
                </button>
            </div>
        );
    }

    return (
        <motion.main
            initial="hidden"
            animate="visible"
            variants={containerVariants}
            className="feedbackContainer"
        >
            {/* Header Section */}
            <motion.header variants={cardVariants} className="header">
                <Link
                    href={user?.role === 'MENTOR' ? '/dashboard/mentor' : '/dashboard/student'}
                    className="backBtn"
                >
                    <ArrowLeft size={18} /> Back to Dashboard
                </Link>
                <h1 className="title">
                    {user?.role === 'MENTOR' ? 'Team Feedback' : 'Your Feedback'}
                </h1>
            </motion.header>

            {/* Feedback List Section */}
            <motion.div className="feedbackList">
                <AnimatePresence>
                    {feedbacks.length > 0 ? (
                        feedbacks.map((item) => (
                            <motion.div
                                key={item.id}
                                variants={cardVariants}
                                whileHover={{ y: -5, boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)' }}
                                className="feedbackCard"
                            >
                                <div className="cardHeader">
                                    <div className="mentorInfo">
                                        <div className="avatar">
                                            {item.mentorName.split(' ').map(n => n[0]).join('')}
                                        </div>
                                        <div>
                                            <span className="mentorName">
                                                <User size={14} style={{ display: 'inline', marginRight: '6px', verticalAlign: 'middle' }} />
                                                {item.mentorName}
                                            </span>
                                            <span className="projectName">
                                                <Briefcase size={12} style={{ display: 'inline', marginRight: '6px', verticalAlign: 'middle' }} />
                                                {item.projectName}
                                            </span>
                                        </div>
                                    </div>
                                    <span className="date">{item.date}</span>
                                </div>
                                <p className="feedbackContent">
                                    <MessageSquare size={16} style={{ display: 'inline', marginRight: '10px', opacity: 0.5, verticalAlign: 'top', marginTop: '4px' }} />
                                    &quot;{item.comment}&quot;
                                </p>
                            </motion.div>
                        ))
                    ) : (
                        /* Empty State */
                        <motion.div variants={cardVariants} className="emptyState">
                            <Inbox size={64} color="#e2e8f0" />
                            <h3 className="emptyTitle">No feedback yet</h3>
                            <p>Keep working on your projects and check back soon!</p>
                            <Link href="/dashboard/student" className="backBtn" style={{ marginTop: '1.5rem', color: '#6366f1' }}>
                                Go build something ΓåÆ
                            </Link>
                        </motion.div>
                    )}
                </AnimatePresence>
            </motion.div>
        </motion.main>
    );
}

'use client';

/**
 * ============================================
 * MENTOR PROJECT MEMBERS MANAGEMENT PAGE
 * Tech Stack: Next.js App Router, TypeScript, CSS, Framer Motion
 * Design: Admin Dashboard, Animated Table, Confirm Modals
 * ============================================
 */

import React, { useState, useEffect, use } from 'react';
import { motion, AnimatePresence, easeOut } from 'framer-motion';
import {
    ArrowLeft,
    Trash2,
    User,
    Mail,
    Shield,
    AlertTriangle,
    CheckCircle2
} from 'lucide-react';
import './MembersManagement.css';

// --- TYPE DEFINITIONS ---

interface Member {
    memberId: string;
    name: string;
    role: 'Student' | 'Mentor';
    email: string;
}

interface PageProps {
    params: Promise<{ projectId: string }>;
}

// --- ANIMATION VARIANTS ---

const rowVariants = {
    hidden: { opacity: 0, y: 10 },
    visible: (i: number) => ({
        opacity: 1,
        y: 0,
        transition: { delay: i * 0.1, duration: 0.4, ease: easeOut }
    }),
    exit: { opacity: 0, x: -20, transition: { duration: 0.3 } }
};

const modalVariants = {
    hidden: { scale: 0.9, opacity: 0 },
    visible: { scale: 1, opacity: 1, transition: { type: "spring", stiffness: 300, damping: 25 } as unknown as any },
    exit: { scale: 0.9, opacity: 0, transition: { duration: 0.2 } }
};

const overlayVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
    exit: { opacity: 0 }
};

// --- MAIN COMPONENT ---

export default function MembersManagementPage({ params }: PageProps) {
    // Standard Next.js way to handle dynamic route params in client components
    const resolvedParams = use(params);
    const projectId = resolvedParams.projectId;

    const [members, setMembers] = useState<Member[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    // Modal State
    const [memberToDelete, setMemberToDelete] = useState<Member | null>(null);
    const [isDeleting, setIsDeleting] = useState(false);

    // Toast State
    const [showSuccess, setShowSuccess] = useState(false);

    // 1. Load Members on Page Load
    useEffect(() => {
        const fetchMembers = async () => {
            try {
                setIsLoading(true);
                // Simulate network latency
                await new Promise(r => setTimeout(r, 1200));

                // Mock response for GET /api/projects/:projectId/members
                const mockMembers: Member[] = [
                    { memberId: 'm1', name: 'Nikhil Reddy', role: 'Student', email: 'nikhil@example.com' },
                    { memberId: 'm2', name: 'Sarah Connor', role: 'Mentor', email: 'sarah@example.com' },
                    { memberId: 'm3', name: 'John Doe', role: 'Student', email: 'john@example.com' },
                    { memberId: 'm4', name: 'Alice Smith', role: 'Student', email: 'alice@example.com' },
                    { memberId: 'm5', name: 'James Wilson', role: 'Mentor', email: 'james@example.com' },
                ];

                setMembers(mockMembers);
            } catch (_err) {
                // error is unused but we keep it in state if needed later
                // setError("Failed to fetch project members. Please try again later.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchMembers();
    }, [projectId]);

    // 2. Handle Delete (Mock API Call)
    const handleDelete = async () => {
        if (!memberToDelete) return;

        try {
            setIsDeleting(true);
            // Simulate DELETE /api/projects/:projectId/members/:memberId
            await new Promise(r => setTimeout(r, 800));

            // Remove from local state
            setMembers(prev => prev.filter(m => m.memberId !== memberToDelete.memberId));

            // Show Success UI
            setMemberToDelete(null);
            setShowSuccess(true);
            setTimeout(() => setShowSuccess(false), 3000);
        } catch (_err) {
            alert("Failed to delete member. Please try again.");
        } finally {
            setIsDeleting(false);
        }
    };

    if (isLoading) {
        return (
            <div className="membersContainer">
                <div className="loadingContainer">
                    <div className="spinner"></div>
                    <p>Loading project members...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="membersContainer">
            {/* Header */}
            <header className="header">
                <a href={`/projects/${projectId}`} className="backBtn">
                    <ArrowLeft size={16} /> Back to Project
                </a>
                <h1 className="title">Project Members</h1>
                <span className="projectIdLabel">Project ID: {projectId}</span>
            </header>

            {/* Members Table */}
            <div className="tableCard">
                <table className="membersTable">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Role</th>
                            <th>Email</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        <AnimatePresence>
                            {members.map((member, index) => (
                                <motion.tr
                                    key={member.memberId}
                                    variants={rowVariants}
                                    initial="hidden"
                                    animate="visible"
                                    exit="exit"
                                    custom={index}
                                >
                                    <td className="memberName">
                                        <User size={14} style={{ marginRight: '8px', verticalAlign: 'middle', color: '#94a3b8' }} />
                                        {member.name}
                                    </td>
                                    <td>
                                        <span className={`memberRole ${member.role === 'Student' ? 'roleStudent' : 'roleMentor'}`}>
                                            <Shield size={12} style={{ marginRight: '4px', verticalAlign: 'middle' }} />
                                            {member.role}
                                        </span>
                                    </td>
                                    <td className="memberEmail">
                                        <Mail size={14} style={{ marginRight: '8px', verticalAlign: 'middle', color: '#94a3b8' }} />
                                        {member.email}
                                    </td>
                                    <td>
                                        <button
                                            className="deleteBtn"
                                            onClick={() => setMemberToDelete(member)}
                                        >
                                            <Trash2 size={14} /> Remove
                                        </button>
                                    </td>
                                </motion.tr>
                            ))}
                        </AnimatePresence>
                    </tbody>
                </table>
                {members.length === 0 && !isLoading && (
                    <div style={{ padding: '3rem', textAlign: 'center', color: '#64748b' }}>
                        No members found in this project.
                    </div>
                )}
            </div>

            {/* Delete Confirmation Modal */}
            <AnimatePresence>
                {memberToDelete && (
                    <motion.div
                        className="modalOverlay"
                        variants={overlayVariants}
                        initial="hidden"
                        animate="visible"
                        exit="exit"
                    >
                        <motion.div
                            className="modalContent"
                            variants={modalVariants}
                        >
                            <div className="modalIcon">
                                <AlertTriangle size={32} />
                            </div>
                            <h2 className="modalTitle">Remove Member?</h2>
                            <p className="modalText">
                                Are you sure you want to remove <strong>{memberToDelete.name}</strong> from the project? This action cannot be undone.
                            </p>
                            <div className="modalActions">
                                <button
                                    className="modalBtn cancelBtn"
                                    onClick={() => setMemberToDelete(null)}
                                    disabled={isDeleting}
                                >
                                    Cancel
                                </button>
                                <button
                                    className="modalBtn confirmDeleteBtn"
                                    onClick={handleDelete}
                                    disabled={isDeleting}
                                >
                                    {isDeleting ? 'Removing...' : 'Confirm Delete'}
                                </button>
                            </div>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Success Success Toast */}
            <AnimatePresence>
                {showSuccess && (
                    <motion.div
                        className="successToast"
                        initial={{ opacity: 0, y: 50, scale: 0.9 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, scale: 0.8 }}
                    >
                        <CheckCircle2 size={18} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
                        Member removed successfully!
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

'use client';

/**
 * ============================================
 * MENTOR PROJECT DETAILS & ANALYTICS PAGE
 * Tech Stack: Next.js App Router, TypeScript, CSS, Framer Motion
 * Design: High-Fidelity SaaS, Animated Grid, Tables
 * ============================================
 */

import React, { useState, useEffect, use } from 'react';
import { motion } from 'framer-motion';
import {
    ArrowLeft,
    Users,
    Plus,
    CheckCircle2,
    Calendar,
    ExternalLink,
    AlertCircle
} from 'lucide-react';
import './MentorProjectDetails.css';

// --- TYPE DEFINITIONS ---

interface Member {
    name: string;
    role: string;
    email: string;
}

interface ProjectDetails {
    projectId: string;
    projectName: string;
    description: string;
    createdAt: string;
    deadline: string;
    status: 'active' | 'completed';
    totalTasks: number;
    completedTasks: number;
    pendingTasks: number;
    members: Member[];
    progressPercentage: number;
}

// --- ANIMATION VARIANTS ---

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: {
            staggerChildren: 0.12,
            delayChildren: 0.2
        }
    }
};

const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: { type: 'spring', stiffness: 260, damping: 20 } as unknown as any
    }
};

// --- MAIN COMPONENT ---

interface PageProps {
    params: Promise<{ projectId: string }>;
}

export default function MentorProjectPage({ params }: PageProps) {
    const resolvedParams = use(params);
    const projectId = resolvedParams.projectId;

    const [project, setProject] = useState<ProjectDetails | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    // Fetch real project data from API
    useEffect(() => {
        const fetchDetails = async () => {
            try {
                setIsLoading(true);
                setError(null);

                // Get user from localStorage for auth headers
                const userStr = localStorage.getItem('user');
                if (!userStr) {
                    setError('Not authenticated');
                    setIsLoading(false);
                    return;
                }

                const user = JSON.parse(userStr);
                const headers = {
                    'x-user-id': user.id,
                    'x-user-role': user.role,
                };

                // Fetch project data
                const projectRes = await fetch('/api/projects', { headers });
                if (!projectRes.ok) {
                    throw new Error('Failed to fetch projects');
                }

                const projects = await projectRes.json();
                const currentProject = projects.find((p: { id: string }) => p.id === projectId);

                if (!currentProject) {
                    setError('Project not found');
                    setIsLoading(false);
                    return;
                }

                // Fetch tasks for this project
                const tasksRes = await fetch('/api/tasks', { headers });
                const tasks = tasksRes.ok ? await tasksRes.json() : [];
                const projectTasks = tasks.filter((t: { projectId: string }) => t.projectId === projectId);

                // Fetch project members
                const membersRes = await fetch(`/api/projects/${projectId}/members`, { headers });
                const membersData = membersRes.ok ? await membersRes.json() : { members: [] };
                const projectMembers = membersData.members || [];

                // Transform members to match component interface
                // Filter to only show students (exclude mentor)
                const transformedMembers: Member[] = projectMembers
                    .filter((m: { role: string }) => m.role === 'STUDENT')
                    .map((m: { name?: string; email?: string }) => ({
                        name: m.name || 'Unknown',
                        role: 'Student',
                        email: m.email || 'No email'
                    }));

                // Calculate task statistics
                const totalTasks = projectTasks.length;
                const completedTasks = projectTasks.filter((t: { status: string }) => t.status === 'DONE').length;
                const pendingTasks = totalTasks - completedTasks;
                const progressPercentage = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

                // Transform to match component interface
                const projectDetails: ProjectDetails = {
                    projectId: currentProject.id,
                    projectName: currentProject.title,
                    description: `Project created on ${new Date(currentProject.createdAt).toLocaleDateString()}`,
                    createdAt: new Date(currentProject.createdAt).toLocaleDateString(),
                    deadline: 'Not set', // API doesn't provide deadline
                    status: 'active',
                    totalTasks,
                    completedTasks,
                    pendingTasks,
                    progressPercentage,
                    members: transformedMembers
                };

                setProject(projectDetails);
            } catch (err: unknown) {
                console.error('Project detail fetch error:', err);
                setError(err instanceof Error ? err.message : "Failed to load project details.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchDetails();
    }, [projectId]);

    if (isLoading) {
        return (
            <div className="loadingContainer">
                <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    className="spinner"
                />
                <p>Fetching project intelligence...</p>
            </div>
        );
    }

    if (error || !project) {
        return (
            <div className="errorContainer">
                <AlertCircle size={48} color="#ef4444" style={{ marginBottom: '1rem' }} />
                <p className="errorText">{error || 'Project not found.'}</p>
                <a href="/dashboard/mentor" className="actionBtn btnGhost" style={{ marginTop: '1.5rem' }}>
                    Return to Dashboard
                </a>
            </div>
        );
    }

    return (
        <motion.div
            initial="hidden"
            animate="visible"
            variants={containerVariants}
            className="mentorDetailsContainer"
        >
            {/* Header Section */}
            <motion.header variants={itemVariants} className="mentorHeader">
                <div className="headerLeft">
                    <a href="/dashboard/mentor" className="btnOutline actionBtn" style={{ padding: '0.5rem 1rem', marginBottom: '1.5rem', border: 'none' }}>
                        <ArrowLeft size={16} /> Back
                    </a>
                    <h1 className="projectName">{project.projectName}</h1>
                    <div className="statusBadge">
                        <CheckCircle2 size={14} style={{ marginRight: '6px' }} />
                        {project.status}
                    </div>
                </div>
                <div className="deadlineBox">
                    <span className="metaLabel">Project Deadline</span>
                    <span className="metaValue">
                        <Calendar size={18} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
                        {project.deadline}
                    </span>
                </div>
            </motion.header>

            {/* Dashboard Grid */}
            <div className="mentorGrid">
                {/* Project Info Card */}
                <motion.section variants={itemVariants} className="mentorCard">
                    <h2 className="cardTitle">Project Overview</h2>
                    <p className="projectDesc">{project.description}</p>
                    <div className="taskStats">
                        <div className="statItem">
                            <span className="statLabel">Total Tasks</span>
                            <span className="statValue">{project.totalTasks}</span>
                        </div>
                        <div className="statItem">
                            <span className="statLabel" style={{ color: '#22c55e' }}>Completed</span>
                            <span className="statValue" style={{ color: '#22c55e' }}>{project.completedTasks}</span>
                        </div>
                        <div className="statItem">
                            <span className="statLabel" style={{ color: '#f59e0b' }}>Pending</span>
                            <span className="statValue" style={{ color: '#f59e0b' }}>{project.pendingTasks}</span>
                        </div>
                    </div>
                </motion.section>

                {/* Analytics Section */}
                <motion.section variants={itemVariants} className="mentorCard">
                    <h2 className="cardTitle">Project Health & Analytics</h2>
                    <div className="heroProgress">
                        <div className="progressLabelRow">
                            <span>Overall Development Progress</span>
                            <span>{project.progressPercentage}%</span>
                        </div>
                        <div className="rail">
                            <motion.div
                                className="fill"
                                initial={{ width: 0 }}
                                animate={{ width: `${project.progressPercentage}%` }}
                                transition={{ duration: 1.5, ease: "circOut", delay: 0.5 }}
                            />
                        </div>
                    </div>
                    <div className="miniStatsRow">
                        <div className="miniCard">
                            <span className="miniLabel">Total Members</span>
                            <span className="miniValue">{project.members.length}</span>
                        </div>
                        <div className="miniCard">
                            <span className="miniLabel">Task Ratio</span>
                            <span className="miniValue">{project.completedTasks}/{project.totalTasks}</span>
                        </div>
                    </div>
                </motion.section>

                {/* Members List Section */}
                <motion.section variants={itemVariants} className="mentorCard" id="team-members-section" style={{ gridColumn: 'span 2' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
                        <h2 className="cardTitle" style={{ margin: 0 }}>Team Members</h2>
                        <a
                            href={`/projects/${projectId}/add-members`}
                            className="btnGhost actionBtn"
                            style={{ padding: '0.4rem 0.8rem', fontSize: '0.75rem', textDecoration: 'none' }}
                        >
                            <Users size={14} /> Add Members
                        </a>
                    </div>
                    <div className="tableWrapper">
                        <table className="membersTable">
                            <thead>
                                <tr>
                                    <th>Full Name</th>
                                    <th>Project Role</th>
                                    <th>Email Address</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {project.members.map((member, idx) => (
                                    <motion.tr
                                        key={idx}
                                        initial={{ opacity: 0, x: -10 }}
                                        animate={{ opacity: 1, x: 0 }}
                                        transition={{ delay: 0.6 + (idx * 0.1) }}
                                    >
                                        <td className="memberName">{member.name}</td>
                                        <td className="memberRole">{member.role}</td>
                                        <td className="memberEmail">{member.email}</td>
                                        <td>
                                            <button className="btnGhost" style={{ padding: '0.3rem', borderRadius: '8px' }}>
                                                <ExternalLink size={14} color="#64748b" />
                                            </button>
                                        </td>
                                    </motion.tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </motion.section>
            </div>

            {/* Action Footer */}
            <motion.footer variants={itemVariants} className="mentorActions">
                <a href="/dashboard/mentor" className="btnOutline actionBtn">
                    ΓåÉ Back to Dashboard
                </a>
                <div className="primaryButtonGroup">
                    <button
                        className="btnGhost actionBtn"
                        onClick={() => {
                            const teamSection = document.querySelector('.mentorCard');
                            if (teamSection) {
                                teamSection.scrollIntoView({ behavior: 'smooth', block: 'start' });
                            }
                        }}
                    >
                        <Users size={18} /> View Student List
                    </button>
                    <a
                        href={`/projects/${projectId}/assign-task`}
                        className="btnSolid actionBtn"
                        style={{ textDecoration: 'none' }}
                    >
                        <Plus size={18} /> Assign New Task
                    </a>
                </div>
            </motion.footer>
        </motion.div>
    );
}

'use client';

import { useState, FormEvent } from 'react';
import { useAuth, getAuthHeaders } from '@/contexts/AuthContext';
import { useRouter } from 'next/navigation';
import ProtectedRoute from '@/components/ProtectedRoute';
import { motion } from 'framer-motion';
import { FolderPlus, ArrowLeft } from 'lucide-react';
import Card from '@/components/ui/Card';
import Button from '@/components/ui/Button';
import Input from '@/components/ui/Input';
import Link from 'next/link';

export default function CreateProjectPage() {
    const { user } = useAuth();
    const [title, setTitle] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');


    const handleSubmit = async (e: FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            const headers = {
                ...getAuthHeaders(user),
                'Content-Type': 'application/json',
            };

            const res = await fetch('/api/projects', {
                method: 'POST',
                headers,
                body: JSON.stringify({ title }),
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.error || 'Failed to create project');
            }


            await res.json();
            // Redirect back to mentor dashboard and force reload to show new project
            window.location.href = '/dashboard/mentor';
        } catch (err: unknown) {
            setError(err instanceof Error ? err.message : 'An error occurred during project creation');
        } finally {
            setIsLoading(false);
        }
    };


    return (
        <ProtectedRoute allowedRoles={['MENTOR']}>
            <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 p-4">
                <div className="max-w-2xl mx-auto pt-12">
                    <Link href="/dashboard/mentor">
                        <Button variant="ghost" size="sm" className="mb-6">
                            <ArrowLeft className="w-4 h-4" />
                            Back to Dashboard
                        </Button>
                    </Link>

                    <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }}>
                        <Card>
                            <div className="flex items-center gap-3 mb-6">
                                <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-500 rounded-xl flex items-center justify-center">
                                    <FolderPlus className="w-6 h-6 text-white" />
                                </div>
                                <div>
                                    <h1 className="text-2xl font-bold text-white">Create New Project</h1>
                                    <p className="text-slate-400 text-sm">Start a new project for your students</p>
                                </div>
                            </div>

                            {error && (
                                <div className="mb-6 p-4 bg-red-500/10 border border-red-500/50 rounded-lg">
                                    <p className="text-red-400 text-sm">{error}</p>
                                </div>
                            )}

                            <form onSubmit={handleSubmit} className="space-y-6">
                                <Input
                                    label="Project Title"
                                    placeholder="Enter project name..."
                                    value={title}
                                    onChange={(e) => setTitle(e.target.value)}
                                    required
                                />

                                <div className="flex gap-3">
                                    <Button type="submit" variant="primary" size="lg" fullWidth isLoading={isLoading}>
                                        Create Project
                                    </Button>
                                    <Link href="/dashboard/mentor">
                                        <Button type="button" variant="ghost" size="lg">
                                            Cancel
                                        </Button>
                                    </Link>
                                </div>
                            </form>
                        </Card>
                    </motion.div>
                </div>
            </div>
        </ProtectedRoute>
    );
}

'use client';

/**
 * ============================================
 * STUDENT TASK DETAILS PAGE (ANIMATED)
 * Tech Stack: Next.js App Router, TypeScript, CSS, Framer Motion
 * Design: Staggered Entrance, Completion Animation
 * ============================================
 */

import React, { useState, useEffect, use } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    ArrowLeft,
    Calendar,
    User,
    Clock,
    Briefcase,
    AlertCircle,
    CheckCircle2,
    Send,
    MoreVertical
} from 'lucide-react';
import './TaskDetails.css';

// --- TYPE DEFINITIONS ---

interface Comment {
    id: string;
    author: string;
    text: string;
    date: string;
}

interface TaskDetails {
    id: string;
    projectId: string;
    projectName: string;
    title: string;
    description: string;
    dueDate: string;
    priority: 'Low' | 'Medium' | 'High';
    status: 'Todo' | 'Doing' | 'Done';
    mentorName: string;
    comments: Comment[];
}

// --- ANIMATION VARIANTS ---

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: { staggerChildren: 0.1 }
    }
};

const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: { type: 'spring', stiffness: 300, damping: 24 } as unknown as any
    }
};

// --- MAIN COMPONENT ---

interface PageProps {
    params: Promise<{ taskId: string }>;
}

export default function TaskDetailsPage({ params }: PageProps) {
    const resolvedParams = use(params);
    const taskId = resolvedParams.taskId;

    const [task, setTask] = useState<TaskDetails | null>(null);
    const [isLoading, setIsLoading] = useState(true);
    const [isUpdating, setIsUpdating] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [successMsg, setSuccessMsg] = useState<string | null>(null);

    useEffect(() => {
        const fetchTaskDetails = async () => {
            try {
                setIsLoading(true);
                setError(null);

                // Get user from localStorage for auth headers
                const userStr = localStorage.getItem('user');
                if (!userStr) {
                    setError('Not authenticated');
                    setIsLoading(false);
                    return;
                }

                const user = JSON.parse(userStr);
                const headers = {
                    'x-user-id': user.id,
                    'x-user-role': user.role,
                };

                // Fetch task details from API
                const taskRes = await fetch(`/api/tasks/${taskId}`, { headers });
                if (!taskRes.ok) {
                    if (taskRes.status === 404) {
                        throw new Error('Task not found');
                    } else if (taskRes.status === 403) {
                        throw new Error('You do not have permission to view this task');
                    }
                    throw new Error('Failed to fetch task details');
                }

                const taskData = await taskRes.json();

                // Fetch project details to get project name
                const projectRes = await fetch('/api/projects', { headers });
                const projects = projectRes.ok ? await projectRes.json() : [];
                const project = projects.find((p: { id: string }) => p.id === taskData.projectId);

                // Transform to match component interface
                const taskDetails: TaskDetails = {
                    id: taskData.id,
                    projectId: taskData.projectId,
                    projectName: project?.title || 'Unknown Project',
                    title: taskData.title,
                    description: 'No description provided', // API doesn't return description yet
                    dueDate: taskData.createdAt ? new Date(taskData.createdAt).toLocaleDateString() : 'Not set',
                    priority: 'Medium', // API doesn't return priority yet
                    status: taskData.status === 'TODO' ? 'Todo' : taskData.status === 'IN_PROGRESS' ? 'Doing' : 'Done',
                    mentorName: 'Mentor', // Would need to fetch from project mentor
                    comments: [] // Would need separate API endpoint for comments
                };

                setTask(taskDetails);
            } catch (err: unknown) {
                console.error('Task fetch error:', err);
                setError(err instanceof Error ? err.message : 'Failed to load task details.');
            } finally {
                setIsLoading(false);
            }
        };
        fetchTaskDetails();
    }, [taskId]);

    const handleMarkAsCompleted = async () => {
        if (!task) return;
        try {
            setIsUpdating(true);
            setError(null);

            // Get user from localStorage for auth headers
            const userStr = localStorage.getItem('user');
            if (!userStr) {
                setError('Not authenticated');
                return;
            }

            const user = JSON.parse(userStr);
            const headers = {
                'x-user-id': user.id,
                'x-user-role': user.role,
                'Content-Type': 'application/json',
            };

            // Update task status via API
            const res = await fetch(`/api/tasks/${task.id}`, {
                method: 'PUT',
                headers,
                body: JSON.stringify({ status: 'DONE' }),
            });

            if (!res.ok) {
                throw new Error('Failed to update task status');
            }

            setTask({ ...task, status: 'Done' });
            setSuccessMsg('Task successfully marked as completed!');
            setTimeout(() => setSuccessMsg(null), 3500);
        } catch (err: unknown) {
            setError(err instanceof Error ? err.message : 'Failed to update task status. Please try again.');
        } finally {
            setIsUpdating(false);
        }
    };

    if (isLoading) {
        return (
            <div className="loadingContainer">
                <motion.div
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    className="spinner"
                />
                <p>Fetching task details...</p>
            </div>
        );
    }

    if (error || !task) {
        return (
            <div className="errorContainer">
                <AlertCircle size={48} color="#ef4444" style={{ marginBottom: '1rem' }} />
                <p className="errorText">{error || 'Task not found.'}</p>
                <a href="/dashboard/student" className="backBtn" style={{ marginTop: '1rem' }}>
                    Return to Dashboard
                </a>
            </div>
        );
    }

    return (
        <motion.div
            initial="hidden"
            animate="visible"
            variants={containerVariants}
            className="detailsContainer"
        >
            <motion.header variants={itemVariants} className="header">
                <a href={`/projects/${task.projectId}`} className="backBtn">
                    <ArrowLeft size={16} /> Back to Project
                </a>
                <div className="titleRow">
                    <h1 className="title">{task.title}</h1>
                    <span className={`badge ${task.status === 'Done' ? 'badgeDone' : task.status === 'Doing' ? 'badgeDoing' : 'badgeTodo'}`}>
                        {task.status === 'Done' ? <CheckCircle2 size={14} style={{ marginRight: '4px' }} /> : <Clock size={14} style={{ marginRight: '4px' }} />}
                        {task.status}
                    </span>
                </div>
            </motion.header>

            <motion.section variants={itemVariants} className="infoCard">
                <div className="descriptionSection">
                    <h3 className="descriptionTitle">Task Description</h3>
                    <p className="description">{task.description}</p>
                </div>

                <div className="metaGrid">
                    <div className="metaItem">
                        <span className="metaLabel">Priority</span>
                        <span className={`priorityBadge ${task.priority === 'High' ? 'priorityHigh' : task.priority === 'Medium' ? 'priorityMedium' : 'priorityLow'}`}>
                            <AlertCircle size={12} style={{ marginRight: '4px' }} />
                            {task.priority}
                        </span>
                    </div>
                    <div className="metaItem">
                        <span className="metaLabel">Due Date</span>
                        <span className="metaValue">
                            <Calendar size={16} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
                            {task.dueDate}
                        </span>
                    </div>
                    <div className="metaItem">
                        <span className="metaLabel">Project</span>
                        <span className="metaValue">
                            <Briefcase size={16} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
                            {task.projectName}
                        </span>
                    </div>
                    <div className="metaItem">
                        <span className="metaLabel">Mentor</span>
                        <span className="metaValue">
                            <User size={16} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
                            {task.mentorName}
                        </span>
                    </div>
                </div>
            </motion.section>

            <motion.div variants={itemVariants} className="actions">
                <button
                    onClick={handleMarkAsCompleted}
                    disabled={task.status === 'Done' || isUpdating}
                    className="completeBtn"
                >
                    {isUpdating ? 'Updating...' : task.status === 'Done' ? 'Completed' : 'Mark as Completed'}
                </button>

                <AnimatePresence>
                    {successMsg && (
                        <motion.div
                            initial={{ opacity: 0, x: -10 }}
                            animate={{ opacity: 1, x: 0 }}
                            exit={{ opacity: 0, scale: 0.95 }}
                            className="successMsg"
                        >
                            <CheckCircle2 size={18} /> {successMsg}
                        </motion.div>
                    )}
                </AnimatePresence>
            </motion.div>

            <motion.section variants={itemVariants} className="section">
                <div className="sectionHeader" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
                    <h2 className="sectionTitle" style={{ margin: 0, border: 'none', padding: 0 }}>Feedback & Comments</h2>
                    <MoreVertical size={20} color="#94a3b8" />
                </div>
                <div className="commentList">
                    <AnimatePresence>
                        {task.comments.length > 0 ? (
                            task.comments.map((comment, idx) => (
                                <motion.div
                                    key={comment.id}
                                    className="commentItem"
                                    initial={{ opacity: 0, y: 10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    transition={{ delay: idx * 0.1 }}
                                    whileHover={{ backgroundColor: '#fdfdfd' }}
                                >
                                    <div className="commentHeader">
                                        <span className="authorName">
                                            <div className="avatarPlaceholder" style={{
                                                width: '32px',
                                                height: '32px',
                                                borderRadius: '50%',
                                                backgroundColor: '#e2e8f0',
                                                display: 'inline-flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                marginRight: '12px',
                                                fontSize: '0.75rem',
                                                fontWeight: 800
                                            }}>
                                                {comment.author.split(' ').map(n => n[0]).join('')}
                                            </div>
                                            {comment.author}
                                        </span>
                                        <span className="commentDate">{comment.date}</span>
                                    </div>
                                    <p className="commentText" style={{ marginLeft: '44px' }}>{comment.text}</p>
                                </motion.div>
                            ))
                        ) : (
                            <div style={{ padding: '3rem', textAlign: 'center', color: '#64748b', background: 'white', borderRadius: '12px', border: '1px solid #e2e8f0' }}>
                                <Send size={32} style={{ marginBottom: '1rem', opacity: 0.2 }} />
                                <p>No comments or feedback yet.</p>
                            </div>
                        )}
                    </AnimatePresence>
                </div>
            </motion.section>
        </motion.div>
    );
}

'use client';

/**
 * ============================================
 * MENTOR CREATE TASK PAGE
 * Tech Stack: Next.js App Router, TypeScript, CSS, Framer Motion
 * Design: High-Fidelity SaaS, Animated Form, Dynamic Fetching
 * ============================================
 */

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    ArrowLeft,
    CheckCircle2,
    AlertCircle,
    Loader2,
    Plus
} from 'lucide-react';
import { useRouter } from 'next/navigation';
import './CreateTask.css';

// --- TYPE DEFINITIONS ---

interface Project {
    id: string;
    name: string;
}

interface Member {
    memberId: string;
    name: string;
    role: 'STUDENT' | 'MENTOR';
}

interface TaskForm {
    title: string;
    description: string;
    projectId: string;
    assignedTo: string;
    dueDate: string;
    priority: 'Low' | 'Medium' | 'High';
}

// --- ANIMATION VARIANTS ---

const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
        opacity: 1,
        transition: { staggerChildren: 0.1, delayChildren: 0.2 }
    }
};

const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
        y: 0,
        opacity: 1,
        transition: { type: 'spring' as const, stiffness: 260, damping: 20 }
    }
};

// --- MAIN COMPONENT ---

export default function CreateTaskPage() {
    const router = useRouter();

    // Form State
    const [formData, setFormData] = useState<TaskForm>({
        title: '',
        description: '',
        projectId: '',
        assignedTo: '',
        dueDate: '',
        priority: 'Medium'
    });

    // Data State
    const [projects, setProjects] = useState<Project[]>([]);
    const [students, setStudents] = useState<Member[]>([]);
    const [isLoadingData, setIsLoadingData] = useState(true);
    const [isSubmitting, setIsSubmitting] = useState(false);

    // Feedback State
    const [successMessage, setSuccessMessage] = useState<string | null>(null);
    const [errorMessage, setErrorMessage] = useState<string | null>(null);

    // 1. Fetch Projects on Load
    useEffect(() => {
        const fetchProjects = async () => {
            try {
                setIsLoadingData(true);

                // Fetch real projects from API
                const response = await fetch('/api/projects', {
                    headers: {
                        'x-user-id': localStorage.getItem('userId') || '',
                        'x-user-role': localStorage.getItem('userRole') || ''
                    }
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch projects');
                }

                const data = await response.json();

                // Map API response to Project interface
                const projectsList: Project[] = data.map((p: any) => ({
                    id: p.id,
                    name: p.title
                }));

                setProjects(projectsList);
            } catch (_err) {
                setErrorMessage("Failed to load projects. Please refresh the page.");
            } finally {
                setIsLoadingData(false);
            }
        };
        fetchProjects();
    }, []);

    // 2. Fetch Students when Project Changes
    useEffect(() => {
        if (!formData.projectId) {
            setStudents([]);
            return;
        }

        const fetchStudents = async () => {
            try {
                // Fetch project members from API
                const response = await fetch(`/api/projects/${formData.projectId}/members`, {
                    headers: {
                        'x-user-id': localStorage.getItem('userId') || '',
                        'x-user-role': localStorage.getItem('userRole') || ''
                    }
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch members');
                }

                const data = await response.json();

                // Extract members array and filter for students only
                const allMembers: Member[] = data.members.map((m: any) => ({
                    memberId: m.id,
                    name: m.name,
                    role: m.role
                }));

                // Filter only students as per requirements
                const projectStudents = allMembers.filter(m => m.role === 'STUDENT');

                setStudents(projectStudents);
                // Reset assignedTo if the previous student is not in the new project
                setFormData(prev => ({ ...prev, assignedTo: '' }));
            } catch (_err) {
                console.error("Failed to fetch students");
                setStudents([]);
            }
        };

        fetchStudents();
    }, [formData.projectId]);

    // 3. Handle Form Changes
    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    // 4. Handle Submission
    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        // Simple Validation
        if (!formData.title || !formData.projectId || !formData.assignedTo || !formData.dueDate) {
            setErrorMessage("Please fill in all required fields.");
            return;
        }

        try {
            setIsSubmitting(true);
            setErrorMessage(null);

            // Send POST request to create task
            const response = await fetch('/api/tasks', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'x-user-id': localStorage.getItem('userId') || '',
                    'x-user-role': localStorage.getItem('userRole') || ''
                },
                body: JSON.stringify({
                    title: formData.title,
                    projectId: formData.projectId,
                    assignedToId: formData.assignedTo
                })
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Failed to create task');
            }

            setSuccessMessage("Task created successfully! Redirecting...");

            // Clear form
            setFormData({
                title: '',
                description: '',
                projectId: '',
                assignedTo: '',
                dueDate: '',
                priority: 'Medium'
            });

            // Redirect after 2 seconds
            setTimeout(() => {
                router.push('/dashboard/mentor');
            }, 2000);

        } catch (err) {
            const errorMessage = err instanceof Error ? err.message : "Failed to create task. Please try again.";
            setErrorMessage(errorMessage);
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <motion.div
            className="createTaskContainer"
            initial="hidden"
            animate="visible"
            variants={containerVariants}
        >
            <div className="header">
                <motion.a variants={itemVariants} href="/dashboard/mentor" className="backBtn">
                    <ArrowLeft size={16} /> Back to Dashboard
                </motion.a>
                <motion.h1 variants={itemVariants} className="title">Create New Task</motion.h1>
                <motion.p variants={itemVariants} className="subtitle">Assign a new responsibility to one of your students.</motion.p>
            </div>

            <motion.div variants={itemVariants} className="formCard">
                <AnimatePresence mode="wait">
                    {successMessage && (
                        <motion.div
                            key="success"
                            className="message success"
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                        >
                            <CheckCircle2 size={18} /> {successMessage}
                        </motion.div>
                    )}
                    {errorMessage && (
                        <motion.div
                            key="error"
                            className="message error"
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                        >
                            <AlertCircle size={18} /> {errorMessage}
                        </motion.div>
                    )}
                </AnimatePresence>

                <form onSubmit={handleSubmit} className="formGrid">
                    {/* Task Title */}
                    <div className="formField">
                        <label className="label">Task Title</label>
                        <input
                            type="text"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                            placeholder="e.g. Implement User Authentication"
                            className="input"
                            required
                        />
                    </div>

                    {/* Description */}
                    <div className="formField">
                        <label className="label">Description</label>
                        <textarea
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            placeholder="Provide details about what needs to be done..."
                            className="textarea"
                        />
                    </div>

                    {/* Project & Student Row */}
                    <div className="formRow">
                        <div className="formField">
                            <label className="label">Project</label>
                            <div style={{ position: 'relative' }}>
                                <select
                                    name="projectId"
                                    value={formData.projectId}
                                    onChange={handleChange}
                                    className="select"
                                    style={{ width: '100%' }}
                                    required
                                >
                                    <option value="">Select Project</option>
                                    {projects.map(p => (
                                        <option key={p.id} value={p.id}>{p.name}</option>
                                    ))}
                                </select>
                                {isLoadingData && (
                                    <Loader2 className="spinner" size={14} style={{ position: 'absolute', right: '35px', top: '12px', animation: 'spin 1s linear infinite' }} />
                                )}
                            </div>
                        </div>

                        <div className="formField">
                            <label className="label">Assign To</label>
                            <select
                                name="assignedTo"
                                value={formData.assignedTo}
                                onChange={handleChange}
                                className="select"
                                disabled={!formData.projectId}
                                required
                            >
                                <option value="">Select Student</option>
                                {students.map(s => (
                                    <option key={s.memberId} value={s.memberId}>{s.name}</option>
                                ))}
                            </select>
                        </div>
                    </div>

                    {/* Deadline & Priority Row */}
                    <div className="formRow">
                        <div className="formField">
                            <label className="label">Due Date</label>
                            <input
                                type="date"
                                name="dueDate"
                                value={formData.dueDate}
                                onChange={handleChange}
                                className="input"
                                required
                            />
                        </div>

                        <div className="formField">
                            <label className="label">Priority</label>
                            <select
                                name="priority"
                                value={formData.priority}
                                onChange={handleChange}
                                className="select"
                            >
                                <option value="Low">Low</option>
                                <option value="Medium">Medium</option>
                                <option value="High">High</option>
                            </select>
                        </div>
                    </div>

                    {/* Submit Button */}
                    <motion.button
                        type="submit"
                        className="submitBtn"
                        disabled={isSubmitting}
                        whileTap={{ scale: 0.98 }}
                    >
                        {isSubmitting ? (
                            <>
                                <div className="btnSpinner"></div>
                                Creating Task...
                            </>
                        ) : (
                            <>
                                <Plus size={18} /> Create Task
                            </>
                        )}
                    </motion.button>
                </form>
            </motion.div>
        </motion.div>
    );
}

'use client';

import { ButtonHTMLAttributes, ReactNode } from 'react';
import { motion } from 'framer-motion';

interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
    children: ReactNode;
    variant?: 'primary' | 'secondary' | 'danger' | 'ghost';
    size?: 'sm' | 'md' | 'lg';
    isLoading?: boolean;
    fullWidth?: boolean;
}

export default function Button({
    children,
    variant = 'primary',
    size = 'md',
    isLoading = false,
    fullWidth = false,
    className = '',
    disabled,
    ...props
}: ButtonProps) {
    const baseStyles = 'font-medium rounded-lg transition-all duration-200 flex items-center justify-center gap-2 disabled:opacity-50 disabled:cursor-not-allowed';

    const variants = {
        primary: 'bg-gradient-to-r from-purple-600 to-purple-700 hover:from-purple-700 hover:to-purple-800 text-white shadow-lg shadow-purple-500/50 hover:shadow-xl hover:shadow-purple-500/60',
        secondary: 'bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white shadow-lg shadow-blue-500/50 hover:shadow-xl hover:shadow-blue-500/60',
        danger: 'bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 text-white shadow-lg shadow-red-500/50 hover:shadow-xl hover:shadow-red-500/60',
        ghost: 'bg-transparent hover:bg-white/10 text-white border border-white/20',
    };

    const sizes = {
        sm: 'px-3 py-1.5 text-sm',
        md: 'px-4 py-2 text-base',
        lg: 'px-6 py-3 text-lg',
    };

    const widthClass = fullWidth ? 'w-full' : '';

    return (
        <motion.button
            whileHover={{ scale: disabled || isLoading ? 1 : 1.02 }}
            whileTap={{ scale: disabled || isLoading ? 1 : 0.98 }}
            className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${widthClass} ${className}`}
            disabled={disabled || isLoading}
            {...(props as any)}
        >
            {isLoading ? (
                <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    <span>Loading...</span>
                </>
            ) : (
                children
            )}
        </motion.button>
    );
}

'use client';

import { InputHTMLAttributes, useState } from 'react';
import { motion } from 'framer-motion';

interface InputProps extends InputHTMLAttributes<HTMLInputElement> {
    label?: string;
    error?: string;
    icon?: React.ReactNode;
}

export default function Input({
    label,
    error,
    icon,
    className = '',
    type = 'text',
    ...props
}: InputProps) {
    const [isFocused, setIsFocused] = useState(false);

    return (
        <div className="w-full">
            {label && (
                <motion.label
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="block text-sm font-medium text-slate-300 mb-2"
                >
                    {label}
                </motion.label>
            )}

            <div className="relative">
                {icon && (
                    <div className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400">
                        {icon}
                    </div>
                )}

                <motion.input
                    whileFocus={{ scale: 1.01 }}
                    type={type}
                    className={`
            w-full px-4 py-3 ${icon ? 'pl-10' : ''}
            bg-slate-800/50 border rounded-lg
            text-white placeholder-slate-400
            transition-all duration-200
            focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent
            ${error ? 'border-red-500' : isFocused ? 'border-purple-500' : 'border-slate-700'}
            ${className}
          `}
                    onFocus={() => setIsFocused(true)}
                    onBlur={() => setIsFocused(false)}
                    {...(props as any)}
                />
            </div>

            {error && (
                <motion.p
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: 0 }}
                    className="mt-1 text-sm text-red-400"
                >
                    {error}
                </motion.p>
            )}
        </div>
    );
}

import mongoose from "mongoose";

/**
 * Mongoose Connection Management
 * ==============================
 * This utility handles the connection to MongoDB using Mongoose.
 * It uses a caching mechanism to ensure that the connection is reused
 * across multiple requests in a serverless environment (like Vercel).
 */

const MONGODB_URI = process.env.DATABASE_URL;

if (!MONGODB_URI) {
    throw new Error(
        "Please define the DATABASE_URL environment variable inside .env"
    );
}

/**
 * Global is used here to maintain a cached connection across hot reloads
 * in development. This prevents connections from growing exponentially
 * during API Route usage.
 */
interface MongooseCache {
    conn: typeof mongoose | null;
    promise: Promise<typeof mongoose> | null;
}

declare global {
    // eslint-disable-next-line no-var
    var mongoose: MongooseCache | undefined;
}

let cached = global.mongoose;

if (!cached) {
    cached = global.mongoose = { conn: null, promise: null };
}

async function dbConnect() {
    if (cached?.conn) {
        return cached.conn;
    }

    if (!cached?.promise) {
        const opts = {
            bufferCommands: false,
        };

        cached!.promise = mongoose.connect(MONGODB_URI!, opts).then((m: typeof mongoose) => {
            return m;
        });
    }

    try {
        cached!.conn = await cached!.promise;
    } catch (e) {
        cached!.promise = null;
        throw e;
    }

    return cached!.conn;
}

export default dbConnect;

/**
 * Enums and Types
 * ===============
 */

export enum Role {
    MENTOR = "MENTOR",
    STUDENT = "STUDENT",
}

export enum TaskStatus {
    TODO = "TODO",
    IN_PROGRESS = "IN_PROGRESS",
    DONE = "DONE",
}

export enum ActionType {
    LOGIN = "LOGIN",
    VIEW_DASHBOARD = "VIEW_DASHBOARD",
    CREATE_PROJECT = "CREATE_PROJECT",
    TASK_UPDATE = "TASK_UPDATE",
    FEEDBACK_SUBMIT = "FEEDBACK_SUBMIT",
}

// Lean Interfaces for Mongoose .lean() results
// These help avoid 'any' usage while satisfying ESLint
export interface LeanUser {
    _id: mongoose.Types.ObjectId;
    name: string;
    email: string;
    password?: string;
    role: Role;
    createdAt: Date;
    updatedAt: Date;
}

export interface LeanProject {
    _id: mongoose.Types.ObjectId;
    title: string;
    mentorId: mongoose.Types.ObjectId;
    createdAt: Date;
    updatedAt: Date;
}

export interface LeanProjectMember {
    _id: mongoose.Types.ObjectId;
    userId: mongoose.Types.ObjectId;
    projectId: mongoose.Types.ObjectId;
    joinedAt: Date;
}

export interface LeanTask {
    _id: mongoose.Types.ObjectId;
    title: string;
    status: TaskStatus;
    projectId: mongoose.Types.ObjectId;
    assignedToId: mongoose.Types.ObjectId;
    createdAt: Date;
    updatedAt: Date;
}

export interface LeanPeerFeedback {
    _id: mongoose.Types.ObjectId;
    projectId: mongoose.Types.ObjectId;
    reviewerId: mongoose.Types.ObjectId;
    revieweeId: mongoose.Types.ObjectId;
    content: string;
    rating: number;
    createdAt: Date;
}

export interface LeanEngagementLog {
    _id: mongoose.Types.ObjectId;
    userId: mongoose.Types.ObjectId;
    actionType: ActionType;
    details: string;
    timestamp: Date;
}

/**
 * Helper: Log Engagement
 */
export async function logEngagement(userId: mongoose.Types.ObjectId, actionType: ActionType, details: string = "") {
    const { EngagementLog } = await import("./models");
    const log = await EngagementLog.create({
        userId,
        actionType,
        details,
        timestamp: new Date(),
    });
    return log as unknown as LeanEngagementLog;
}

import mongoose, { Schema, model } from "mongoose";

// User Schema
const UserSchema = new Schema({
    name: { type: String, required: true },
    email: { type: String, required: true, unique: true },
    password: { type: String, required: true },
    role: { type: String, enum: ['STUDENT', 'MENTOR'], default: 'STUDENT' },
}, { timestamps: true });

// Project Schema
const ProjectSchema = new Schema({
    title: { type: String, required: true },
    mentorId: { type: Schema.Types.ObjectId, ref: 'User', required: true },
}, { timestamps: true });

// ProjectMember Schema
const ProjectMemberSchema = new Schema({
    userId: { type: Schema.Types.ObjectId, ref: 'User', required: true },
    projectId: { type: Schema.Types.ObjectId, ref: 'Project', required: true },
    joinedAt: { type: Date, default: Date.now },
});
ProjectMemberSchema.index({ userId: 1, projectId: 1 }, { unique: true });

// Task Schema
const TaskSchema = new Schema({
    title: { type: String, required: true },
    status: { type: String, enum: ['TODO', 'IN_PROGRESS', 'DONE'], default: 'TODO' },
    projectId: { type: Schema.Types.ObjectId, ref: 'Project', required: true },
    assignedToId: { type: Schema.Types.ObjectId, ref: 'User' },
}, { timestamps: true });

// PeerFeedback Schema
const PeerFeedbackSchema = new Schema({
    rating: { type: Number, required: true, min: 1, max: 5 },
    comment: { type: String, required: true },
    projectId: { type: Schema.Types.ObjectId, ref: 'Project', required: true },
    fromUserId: { type: Schema.Types.ObjectId, ref: 'User', required: true },
    toUserId: { type: Schema.Types.ObjectId, ref: 'User', required: true },
}, { timestamps: true });

// EngagementLog Schema
const EngagementLogSchema = new Schema({
    actionType: { type: String, required: true },
    details: { type: String },
    userId: { type: Schema.Types.ObjectId, ref: 'User', required: true },
}, { timestamps: true });

// Models Initialization
export const User = mongoose.models.User || mongoose.model('User', UserSchema);
export const Project = mongoose.models.Project || mongoose.model('Project', ProjectSchema);
export const ProjectMember = mongoose.models.ProjectMember || mongoose.model('ProjectMember', ProjectMemberSchema);
export const Task = mongoose.models.Task || mongoose.model('Task', TaskSchema);
export const PeerFeedback = mongoose.models.PeerFeedback || mongoose.model('PeerFeedback', PeerFeedbackSchema);
export const EngagementLog = mongoose.models.EngagementLog || mongoose.model('EngagementLog', EngagementLogSchema);
